sys_script_include.script:
var CommentTemplateUtil = Class.create();
CommentTemplateUtil.prototype = {
    initialize: function() {
    },

    /**
     *
     * @param {*} templateGR
     * @param {*} recordGR
     * @example
     *
      var ctUtil = new x_184695_ct.CommentTemplateUtil()
      var template = new GlideRecord('x_184695_ct_comment_template');
      template.get('50e880292f2101101845deb6f699b672');

      var incident = new GlideRecord('incident');
      incident.get('95f518652f6101101845deb6f699b67b');

      ctUtil.applyTemplate(template, incident);
     * @returns
     */
    applyTemplate: function (templateGR, recordGR) {
      var templateString = templateGR.html_message.getHTMLValue();

      var c2html = new x_184695_ct.commentTemplateToHTML(
        recordGR.getTableName(),
        recordGR.getValue("sys_id"),
        templateString
      );

      var t = c2html.toString();

     recordGR.comments = "[code]" + t + "[/code]";
     recordGR.update()
     return t;
    },

    type: 'CommentTemplateUtil'
};
